import React from 'react';
import Modal from 'react-bootstrap/Modal'
import Button from 'react-bootstrap/Button'
import 'bootstrap/dist/css/bootstrap.min.css';





class SelectedBeast extends React.Component {
    constructor(props){
        super (props);
        console.log(this.props.cardsBeasts);
        let filterdeBeast = this.props.cardsBeasts.filter(element => {
            return (this.props.title == element.title)
        });
        console.log(filterdeBeast);

      }
    render() {

        // chosenPic = this.props.cardsBeasts.filter((element) => {

        // })

        return (

            <div>

                <Modal show={this.props.show} onHide={this.props.handleClose}>
                    <Modal.Header closeButton>
                        <Modal.Title>{this.props.title}</Modal.Title>
                    </Modal.Header>
                    <div>

                        <img src={filterdeBeast} />
                        <Modal.Body>
                            {this.props.description}
                        </Modal.Body>
                    </div>
                    <Modal.Footer>
                        <Button  onClick={() =>this.props.handleClose()}>
                            Close
                        </Button>
                        <Button onClick={() => this.props.handleClose()}>
                            Save Changes
                        </Button>
                    </Modal.Footer>
                </Modal>
            </div>
        )

    }
}

export default SelectedBeast;
