import React from 'react'
import Card from 'react-bootstrap/Card'
import Button from 'react-bootstrap/Button'


class Hornedbeast extends React.Component {

    constructor() {
        super()
        this.state = {
            numOfVotes: 0
        }
    }

    gotVoted = () => {
        this.setState({
            numOfVotes: this.state.numOfVotes + 1
        })
    }
    render() {
        return (
            <div>
{/* 
                // <h2>{this.props.title}</h2>
                // <img height='200 ' onClick={this.gotVoted} src={this.props.imgUrl} alt='horned beast img' />
                // <p>{this.props.description}</p> */}

                <Card style={{ width: '18rem' }}>
                    <Card.Img variant="top"  onClick={this.gotVoted} src={this.props.imgUrl} />
                    <Card.Body>
                        <Card.Title>{this.props.title}</Card.Title>
                        <Card.Text>
                         {this.props.description}
                         <h4>❤️ : {this.state.numOfVotes}</h4>
                        </Card.Text>     
                        <Button variant="primary">Go somewhere</Button>                   
                    </Card.Body>
                </Card>
            </div>
        )
    }
}


export default Hornedbeast;
