              })
        }
    }


    render() {

        let allCardArr = this.state.displayedImg.map(element => {
            return (<Hornedbeast
                takeData={this.props.takeData}
                title={element.title}
                imgUrl={element.image_url}
                description={element.description}
                handleClose={this.props.handleClose}
            />
